import React, { useState } from "react";

const MatchingForm = () => {
  const [formData, setFormData] = useState({
    name: "",
    introduction: "",
    grade: "",
  });
  const [isLoading, setIsLoading] = useState(false);
  const [message, setMessage] = useState({ type: "", content: "" });

  const grades = [
    "大一",
    "大二",
    "大三",
    "大四",
    "研究生一年级",
    "研究生二年级",
    "研究生三年级",
    "博士生",
  ];

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setMessage({ type: "", content: "" });

    try {
      // 模拟API调用
      await new Promise((resolve) => setTimeout(resolve, 1500));
      setMessage({
        type: "success",
        content: "提交成功！正在为您匹配...",
      });
    } catch (error) {
      setMessage({
        type: "error",
        content: "提交失败，请稍后重试",
      });
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-primary-50 to-primary-100">
      <div className="container mx-auto px-4 py-16">
        <div className="max-w-xl mx-auto">
          {/* 卡片容器 */}
          <div className="bg-white rounded-2xl shadow-xl overflow-hidden transform transition-all hover:shadow-2xl">
            {/* 头部装饰 */}
            <div className="h-2 bg-gradient-to-r from-primary-400 to-primary-600"></div>

            {/* 内容区域 */}
            <div className="px-8 py-6">
              {/* 头部 */}
              <div className="text-center mb-8">
                <h2 className="text-3xl font-bold text-gray-900 mb-3">
                  个人匹配
                </h2>
                <div className="flex justify-center mb-4">
                  <div className="w-20 h-1 bg-primary-500 rounded-full"></div>
                </div>
                <p className="text-gray-600">
                  请填写您的个人信息，我们将为您找到最合适的匹配
                </p>
              </div>

              {/* 消息提示 */}
              {message.content && (
                <div
                  className={`mb-6 rounded-lg p-4 border ${
                    message.type === "success"
                      ? "bg-green-50 border-green-200 text-green-700"
                      : "bg-red-50 border-red-200 text-red-700"
                  } transform transition-all duration-300 ease-in-out`}
                >
                  <p className="text-sm font-medium">{message.content}</p>
                </div>
              )}

              {/* 表单 */}
              <form className="space-y-6" onSubmit={handleSubmit}>
                {/* 姓名输入 */}
                <div className="space-y-2">
                  <label
                    htmlFor="name"
                    className="block text-sm font-medium text-gray-700"
                  >
                    姓名
                  </label>
                  <input
                    id="name"
                    name="name"
                    type="text"
                    required
                    value={formData.name}
                    onChange={handleChange}
                    className="block w-full px-4 py-3 rounded-lg border border-gray-300 shadow-sm focus:ring-2 focus:ring-primary-400 focus:border-primary-400 transition-all duration-200"
                    placeholder="请输入您的姓名"
                  />
                </div>

                {/* 年级选择 */}
                <div className="space-y-2">
                  <label
                    htmlFor="grade"
                    className="block text-sm font-medium text-gray-700"
                  >
                    年级
                  </label>
                  <select
                    id="grade"
                    name="grade"
                    required
                    value={formData.grade}
                    onChange={handleChange}
                    className="block w-full px-4 py-3 rounded-lg border border-gray-300 shadow-sm focus:ring-2 focus:ring-primary-400 focus:border-primary-400 transition-all duration-200"
                  >
                    <option value="">请选择年级</option>
                    {grades.map((grade) => (
                      <option key={grade} value={grade}>
                        {grade}
                      </option>
                    ))}
                  </select>
                </div>

                {/* 自我介绍 */}
                <div className="space-y-2">
                  <label
                    htmlFor="introduction"
                    className="block text-sm font-medium text-gray-700"
                  >
                    自我介绍
                  </label>
                  <textarea
                    id="introduction"
                    name="introduction"
                    rows="4"
                    required
                    value={formData.introduction}
                    onChange={handleChange}
                    className="block w-full px-4 py-3 rounded-lg border border-gray-300 shadow-sm focus:ring-2 focus:ring-primary-400 focus:border-primary-400 transition-all duration-200 resize-none"
                    placeholder="请简单介绍一下自己..."
                  />
                </div>

                {/* 提交按钮 */}
                <div className="pt-4">
                  <button
                    type="submit"
                    disabled={isLoading}
                    className={`w-full py-3 px-6 rounded-lg text-white font-medium transition-all duration-200 transform hover:scale-[1.02] ${
                      isLoading
                        ? "bg-primary-400 cursor-not-allowed"
                        : "bg-gradient-to-r from-primary-500 to-primary-600 hover:from-primary-600 hover:to-primary-700 shadow-lg hover:shadow-xl focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500"
                    }`}
                  >
                    {isLoading ? (
                      <span className="flex items-center justify-center">
                        <svg
                          className="animate-spin -ml-1 mr-3 h-5 w-5 text-white"
                          xmlns="http://www.w3.org/2000/svg"
                          fill="none"
                          viewBox="0 0 24 24"
                        >
                          <circle
                            className="opacity-25"
                            cx="12"
                            cy="12"
                            r="10"
                            stroke="currentColor"
                            strokeWidth="4"
                          ></circle>
                          <path
                            className="opacity-75"
                            fill="currentColor"
                            d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"
                          ></path>
                        </svg>
                        处理中...
                      </span>
                    ) : (
                      "开始匹配"
                    )}
                  </button>
                </div>
              </form>
            </div>
          </div>

          {/* 底部装饰 */}
          <div className="mt-8 text-center text-sm text-gray-500">
            © 2024 个人匹配系统 · 让缘分不再错过
          </div>
        </div>
      </div>
    </div>
  );
};

export default MatchingForm;
